public class Lab047 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		//Result, a
		/*
		// pre
		  System.out.println(++a);
		// Result-->11
			//a-->11
		  
		  System.out.println(a);
		  */
		  
		// post
		  System.out.println(a++);
		// Result-->10
			//a-->11
		  
		  System.out.println(a);
		  
		  
		  //++ in front means  --> do Increment first then print  -->pre
		   
		   // ++ in last means --> print first then  do Increment   --> post
		   

		  
		  
	}

}
