package ex_27042024;

public class Lab116 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j=1 ; j<=10 ;j=j+1) { //1 to 10
			 System.out.println(j);
		}
		
		for(int j=10 ; j>0 ;j--) { //j-- or --j --> both same result
			 System.out.println(j);
		}
	}

}
